let main = document.querySelector('#container');
let styleText = document.querySelector('#style');

let str1 = `
/*我这是一段演示*/
div {
    color: #303133;
}

/*开始画圆并给背景一个渐变填充*/
.circle {
  height: 300px;
  width: 300px;
  border-radius: 50%;
  box-shadow: 0 0 3px rgba(0, 0, 0, .3);
  background: linear-gradient(90deg, rgba(255, 255, 255, 1) 0%, rgba(255, 255, 255, 1) 50%, rgba(0, 0, 0, 1) 50%, rgba(0, 0, 0, 1) 100%);
  position: relative;
}
/*添加两个大圆,一上一下,用径向渐变使中间的圆显现出来*/
.circle::before {
  content: '';
  position: absolute;
  height: 150px;
  width: 150px;
  top: 0;
  left: 50%;
  transform: translateX(-50%);
  border-radius: 50%;
  background: radial-gradient(circle, rgba(255, 255, 255, 1) 0%, rgba(255, 255, 255, 1) 25%, rgba(0, 0, 0, 1) 25%, rgba(0, 0, 0, 1) 100%);
}

.circle::after {
  content: '';
  position: absolute;
  height: 150px;
  width: 150px;
  bottom: 0;
  left: 50%;
  transform: translateX(-50%);
  background: radial-gradient(circle, rgba(0, 0, 0, 1) 0%, rgba(0, 0, 0, 1) 25%, rgba(255, 255, 255, 1) 25%, rgba(255, 255, 255, 1) 100%);
  border-radius: 50%;
}
`;

let str2 = ``;
let n = -1;

function step() {
    setTimeout(() => {
        n = n + 1;
        if (n < str1.length) {
            if (str1[n] === '\n') {
                str2 += '<br>'
            } else if (str1[n] === ' ') {
                str2 += '&nbsp'
            } else {
                str2 += str1[n]
            }
            main.innerHTML = str2
            styleText.innerHTML = str1.substring(0, str2.length);
            step();
            main.scrollTo(0, 9999)
        }
    }, 20)
}
step()
